import React, { useState, useEffect } from "react";

function App() {
  const [gratitude, setGratitude] = useState("");
  const [entries, setEntries] = useState([]);

  // Carica dati salvati dal localStorage
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("gratitudeEntries")) || [];
    setEntries(saved);
  }, []);

  // Salva i dati nel localStorage ogni volta che cambia entries
  useEffect(() => {
    localStorage.setItem("gratitudeEntries", JSON.stringify(entries));
  }, [entries]);

  const handleSave = () => {
    if (gratitude.trim() === "") return;
    const newEntries = [
      { text: gratitude, date: new Date().toLocaleDateString() },
      ...entries,
    ];
    setEntries(newEntries);
    setGratitude("");
  };

  return (
    <div className="flex flex-col items-center justify-start min-h-screen bg-gradient-to-b from-blue-50 to-blue-100 p-4">
      <h1 className="text-3xl sm:text-4xl font-semibold text-blue-700 text-center mt-6 mb-4">
        Diario della Gratitudine ðŸŒž
      </h1>

      <p className="text-center text-gray-600 mb-4 text-base sm:text-lg max-w-sm">
        Scrivi ogni giorno tre cose di cui sei grato. I tuoi pensieri restano
        solo sul tuo dispositivo ðŸ’­
      </p>

      <textarea
        value={gratitude}
        onChange={(e) => setGratitude(e.target.value)}
        className="w-full max-w-md h-40 p-3 border border-gray-300 rounded-xl text-gray-700 text-base resize-none shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        placeholder="Scrivi qui le tue riflessioni di oggi..."
      ></textarea>

      <button
        onClick={handleSave}
        className="mt-4 w-full max-w-md py-3 bg-blue-600 text-white text-lg font-medium rounded-xl hover:bg-blue-700 active:scale-95 transition"
      >
        Salva la giornata âœ¨
      </button>

      <h2 className="text-xl font-semibold text-blue-700 mt-8 mb-2">
        Le tue note recenti
      </h2>

      <div className="flex flex-col gap-3 w-full max-w-md">
        {entries.length === 0 && (
          <p className="text-gray-500 text-center italic">
            Nessuna voce ancora. Inizia oggi!
          </p>
        )}
        {entries.map((entry, index) => (
          <div
            key={index}
            className="bg-white shadow-md rounded-lg p-3 border border-gray-200"
          >
            <p className="text-gray-800 whitespace-pre-wrap">{entry.text}</p>
            <p className="text-sm text-gray-500 mt-1 text-right">
              {entry.date}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
