import React, { useState, useEffect } from "react";

function App() {
  const [gratitude, setGratitude] = useState("");
  const [entries, setEntries] = useState([]);

  // Carica i dati salvati
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("gratitudeEntries")) || [];
    setEntries(saved);
  }, []);

  // Salva ogni modifica
  useEffect(() => {
    localStorage.setItem("gratitudeEntries", JSON.stringify(entries));
  }, [entries]);

  const handleSave = () => {
    if (gratitude.trim() === "") return;
    const newEntries = [
      { text: gratitude, date: new Date().toLocaleDateString("it-IT") },
      ...entries,
    ];
    setEntries(newEntries);
    setGratitude("");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-gradient-to-b from-yellow-50 to-yellow-100 px-4 py-8">
      {/* HEADER */}
      <div className="w-full max-w-md text-center mb-8">
        <h1 className="text-3xl sm:text-4xl font-bold text-gray-800 mb-2">
          Diario di Leo ðŸŒž
        </h1>
        <p className="text-gray-600 text-base sm:text-lg">
          Rispondi alle domande riflettendo sulla tua giornata di oggi.
        </p>
      </div>

      {/* BLOCCO DI INSERIMENTO */}
      <div className="w-full max-w-md bg-white rounded-2xl shadow-md p-5 mb-8 text-center">
        <h2 className="text-lg font-semibold text-gray-700 mb-3">
          Seleziona la data del tuo diario
        </h2>
        <div className="flex flex-col sm:flex-row items-center justify-center gap-3">
          <input
            type="text"
            value={new Date().toLocaleDateString("it-IT")}
            readOnly
            className="w-full sm:w-1/2 text-center border border-gray-300 rounded-lg py-2 px-3 text-gray-700 focus:outline-none focus:ring-2 focus:ring-orange-400"
          />
          <button
            onClick={() => window.scrollTo({ top: 500, behavior: "smooth" })}
            className="w-full sm:w-1/2 bg-orange-500 hover:bg-orange-600 text-white text-lg font-semibold py-2 rounded-lg transition"
          >
            Inizia âœ¨
          </button>
        </div>
      </div>

      {/* AREA DOMANDE */}
      <div className="w-full max-w-md flex flex-col gap-6">
        <div className="bg-yellow-100 border border-yellow-200 rounded-2xl p-5 shadow-sm">
          <h3 className="text-xl font-semibold text-gray-800 mb-3">
            Ripensando alla giornata di oggi, fai un elenco delle cose per cui sei grato ðŸ’›
          </h3>
          <textarea
            value={gratitude}
            onChange={(e) => setGratitude(e.target.value)}
            className="w-full h-32 p-3 border border-gray-300 rounded-xl text-gray-700 text-base resize-none shadow-sm focus:outline-none focus:ring-2 focus:ring-yellow-400"
            placeholder="Scrivi qui le cose per cui sei grato oggi..."
          ></textarea>
          <button
            onClick={handleSave}
            className="mt-4 w-full py-3 bg-yellow-500 text-white text-lg font-medium rounded-xl hover:bg-yellow-600 active:scale-95 transition"
          >
            Salva riflessione ðŸŒ¼
          </button>
        </div>
      </div>

      {/* SEZIONE RISPOSTE */}
      <div className="w-full max-w-md mt-10">
        <h2 className="text-2xl font-bold text-gray-800 text-center mb-4">
          Le tue risposte salvate
        </h2>
        <div className="flex flex-col gap-3">
          {entries.length === 0 && (
            <p className="text-gray-500 text-center italic">
              Nessuna risposta ancora. Inizia a scrivere la tua prima riflessione ðŸ’­
            </p>
          )}
          {entries.map((entry, index) => (
            <div
              key={index}
              className="bg-white shadow-sm rounded-xl p-4 border border-gray-200"
            >
              <p className="text-gray-800 whitespace-pre-wrap text-base leading-relaxed">
                {entry.text}
              </p>
              <p className="text-sm text-gray-500 mt-2 text-right">
                {entry.date}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
